import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";

import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function App() {
  const [file, setFile] = useState(null);
  const [dataset, setDataset] = useState(null);
  const [history, setHistory] = useState([]);
  const [error, setError] = useState(""); // ðŸ‘ˆ THIS was missing

  const backendUrl = "http://127.0.0.1:8000/api";

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    setError("");
    if (!file) {
      setError("Please select a CSV file first.");
      return;
    }

    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await axios.post(`${backendUrl}/upload/`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Upload response:", res.data);
      setDataset(res.data);
      fetchHistory();
    } catch (err) {
      console.error(err);
      setError(
        err.response?.data?.error ||
          err.response?.data?.detail ||
          "Upload failed. Check console for details."
      );
    }
  };

  const fetchHistory = async () => {
    setError("");
    try {
      const res = await axios.get(`${backendUrl}/datasets/`);
      console.log("History response:", res.data);
      setHistory(res.data);
    } catch (err) {
      console.error(err);
      setError(
        err.response?.data?.detail ||
          "Failed to fetch history. Check console for details."
      );
    }
  };

  useEffect(() => {
    fetchHistory();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getChartData = () => {
    if (!dataset || !dataset.summary) return null;
    const dist = dataset.summary.type_distribution || {};
    const labels = Object.keys(dist);
    const counts = Object.values(dist);

    return {
      labels,
      datasets: [
        {
          label: "Equipment Count by Type",
          data: counts,
        },
      ],
    };
  };

  const renderSummary = () => {
    if (!dataset || !dataset.summary) return null;
    const s = dataset.summary;
    return (
      <div>
        <p>Total Count: {s.total_count ?? "-"}</p>
        <p>
          Average Flowrate:{" "}
          {s.average_flowrate !== undefined
            ? s.average_flowrate.toFixed(2)
            : "-"}
        </p>
        <p>
          Average Pressure:{" "}
          {s.average_pressure !== undefined
            ? s.average_pressure.toFixed(2)
            : "-"}
        </p>
        <p>
          Average Temperature:{" "}
          {s.average_temperature !== undefined
            ? s.average_temperature.toFixed(2)
            : "-"}
        </p>
      </div>
    );
  };

  const renderTable = () => {
    if (!dataset || !dataset.preview_rows || dataset.preview_rows.length === 0) {
      return <p>No preview data available.</p>;
    }

    const columns = Object.keys(dataset.preview_rows[0] || {});

    return (
      <table border="1">
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {dataset.preview_rows.map((row, idx) => (
            <tr key={idx}>
              {columns.map((col) => (
                <td key={col}>{row[col]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  const chartData = getChartData();

  return (
    <div style={{ padding: "20px" }}>
      <h1>Chemical Equipment Parameter Visualizer (Web)</h1>

      {error && (
        <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>
      )}

      <div style={{ marginBottom: "20px" }}>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        <button onClick={handleUpload} style={{ marginLeft: "10px" }}>
          Upload CSV
        </button>
      </div>

      <h2>Latest Dataset Summary</h2>
      {renderSummary()}

      {chartData && (
        <div style={{ width: "400px", height: "300px", marginTop: "20px" }}>
          <Bar data={chartData} />
        </div>
      )}

      <h3 style={{ marginTop: "20px" }}>Preview (First 10 Rows)</h3>
      {renderTable()}

      <h2 style={{ marginTop: "30px" }}>Last 5 Datasets</h2>
      <ul>
        {history.map((d) => (
          <li key={d.id}>
            {d.name} â€“ {new Date(d.uploaded_at).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
