import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function App() {
  const [file, setFile] = useState(null);
  const [dataset, setDataset] = useState(null);
  const [history, setHistory] = useState([]);

  //const backendUrl = 'http://127.0.0.1:8000/api';
  const backendUrl = "http://192.168.31.195:8000/api";


  /*const authConfig = {
    auth: {
      username: 'your_username',  // for demo; later replace with proper login form
      password: 'your_password',
    },
  };*/

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  /*const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);

    const res = await axios.post(`${backendUrl}/upload/`, formData, {
      ...authConfig,
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    setDataset(res.data);
    fetchHistory();
  };*/

  const fetchHistory = async () => {
    const res = await axios.get(`${backendUrl}/datasets/`, authConfig);
    setHistory(res.data);
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  const getChartData = () => {
    if (!dataset) return null;
    const dist = dataset.summary.type_distribution;
    const labels = Object.keys(dist);
    const counts = Object.values(dist);

    return {
      labels,
      datasets: [
        {
          label: 'Equipment Count by Type',
          data: counts,
        },
      ],
    };
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Chemical Equipment Parameter Visualizer (Web)</h1>

      <div>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload CSV</button>
      </div>

      <h2>Latest Dataset Summary</h2>
      {dataset && (
        <div>
          <p>Total Count: {dataset.summary.total_count}</p>
          <p>Average Flowrate: {dataset.summary.average_flowrate.toFixed(2)}</p>
          <p>Average Pressure: {dataset.summary.average_pressure.toFixed(2)}</p>
          <p>Average Temperature: {dataset.summary.average_temperature.toFixed(2)}</p>

          {getChartData() && (
            <div style={{ width: '400px', height: '300px' }}>
              <Bar data={getChartData()} />
            </div>
          )}

          <h3>Preview (First 10 Rows)</h3>
          <table border="1">
            <thead>
              <tr>
                {Object.keys(dataset.preview_rows[0] || {}).map((col) => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {dataset.preview_rows.map((row, idx) => (
                <tr key={idx}>
                  {Object.values(row).map((val, j) => (
                    <td key={j}>{val}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <h2>Last 5 Datasets</h2>
      <ul>
        {history.map((d) => (
          <li key={d.id}>
            {d.name} â€“ {new Date(d.uploaded_at).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
